import React from "react";

const ProfileStatusWithHooks = (props) => {
    return (
        <div>
            {
                <div>
                    <span onDoubleClick={this.activateEditMode}>{props.status || '-----'}</span>
                </div>
            }
            {false &&
                <div>
                    <input onChange={this.onStatusChange} autoFocus={true} value={''} />
                    <button>Save</button>
                </div>}
        </div>
    );
}
export default ProfileStatusWithHooks;
