import React, { useState } from "react";
import s from './Paginator.module.css'

const Paginator = (props) => {
    let pagesCount = Math.ceil(props.totalItemsCount / props.pageSize);
    let pages = [];
    for (let i = 1; i < pagesCount; i++) {
        pages.push(i);
    }
    let portionCount = Math.ceil(pagesCount / props.portionSize);
    let [portionNumber, setPortionNumber] = useState(1);
    let leftPortionPageNumber = (portionNumber - 1) * props.portionSize + 1;
    let rightPortionPageNumber = portionNumber * props.portionSize
    let onNextButtonClick = () => {
        setPortionNumber(portionNumber + 1)
        props.setCurrentPage(portionNumber * props.pageSize - 9 )
    }
    let onPrevButtonClick = () => {
        setPortionNumber(portionNumber - 1)
        props.setCurrentPage(props.currentPage)
    }
    return (
        <div>
            { <button className={s.buttonPaginator} disabled={portionNumber === 1} onClick={onPrevButtonClick}>PREV</button>}
                {pages.filter(p => p >= leftPortionPageNumber && p <= rightPortionPageNumber)
                .map((p) => {
                    return <span key={p} className={s.item + ' ' + (props.currentPage === p ? s.selectedPage : '')}
                        onClick={(e) => { props.onPageChanged(p); }}>{p}</span>
                })}
            {<button className={s.buttonPaginator} disabled={portionNumber == portionCount} onClick={onNextButtonClick}>NEXT</button> }
        </div>
    )
}


export default Paginator;
