import React from "react";
import s from "./FormsControls.module.css"
export const Textarea = ({input, meta, ...props}) => {
    return (
        <div className={s.formControl + " " + s.error}>
            <div>
                <textarea {...input} {...props} />
            </div>
            {meta.touched && meta.error && <span>"some error"</span>}
        </div>
    )
}
