import React from "react";
import s from './Users.module.css'

const Users = (props) => {
    if (props.users.length === 0) {
        props.setUsers ( {users: [
            {id: 1, photoUrl: 'https://m.media-amazon.com/images/I/61aeiNimLJL._AC_SY606_.jpg', followed: true, fullname: 'Karl', status: 'Oh no, Kaaarl, Kaaarl', location: {country: 'USA', city: 'New-York'} },
            {id: 2, photoUrl: 'https://m.media-amazon.com/images/I/61aeiNimLJL._AC_SY606_.jpg', followed: false, fullname: 'Bob', status: 'I\'m not soup', location: {country: 'Canada', city: 'Bratslit'} },
            {id: 3, photoUrl: 'https://m.media-amazon.com/images/I/61aeiNimLJL._AC_SY606_.jpg', followed: false, fullname: 'Ivan', status: 'Kalinka malinka', location: {country: 'Russia', city: 'Moscow'} },
            {id: 4, photoUrl: 'https://m.media-amazon.com/images/I/61aeiNimLJL._AC_SY606_.jpg', followed: true, fullname: 'Vasya', status: 'Putin Huilo', location: {country: 'Ukraine', city: 'Kiev'} }
        ]
    });
    }
    return (
        <div>
            {props.users.map ( u => <div key={u.id}>
                <span>
                    <div>
                        <img src={u.photoUrl} className={s.userPhoto}></img>
                    </div>
                    <div>
                        { u.followed 
                            ? <button onClick={ () => { props.unfollow(u.id) } }>Unfollow</button>
                            : <button onClick={ () => { props.follow(u.id) } }>Follow</button>}
                    </div>
                </span>
                <span>
                    <span>
                        <div>{u.fullName}</div>
                        <div>{u.status}</div>

                    </span>
                    <span>
                        <div>{u.location.country}</div>
                        <div>{u.location.city}</div>
                    </span>
                </span>
            </div>)}
        </div>
    )
}
export default Users;
