const SET_USERS = 'SET-USERS';
const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';


let initialState = {
    users: [
        {id: 1, followed: false, fullname: 'Karl', status: 'Oh no, Kaaarl, Kaaarl', location: {country: 'USA', city: 'New-York'} },
        {id: 2, followed: false, fullname: 'Bob', status: 'I\'m not soup', location: {country: 'Canada', city: 'Bratslit'} },
        {id: 3, followed: false, fullname: 'Ivan', status: 'Kalinka malinka', location: {country: 'Russia', city: 'Moscow'} },
        {id: 4, followed: true, fullname: 'Vasya', status: 'Putin Huilo', location: {country: 'Ukraine', city: 'Kiev'} }
    ]
}

const usersReducer = (state = initialState, action) => {
    switch(action.type) {
        case FOLLOW:
            return {
                ...state, 
                users: state.users.map(u =>  {
                    if (u.id === action.userid) {
                        return {...u, followed: true}
                    }
                    return u;
                }) 
            }
        case UNFOLLOW:
            return {
                ...state, 
                users: state.users.map(u =>  {
                    if (u.id === action.userid) {
                        return {...u, followed: false}
                    }
                    return u;
                }) 
            }
        case SET_USERS: {
                return {
                    ...state, users: [...state.users, ...action.users]
                }
            }
        default: state;
    }
}
export const setUsersAC = (users) => ({type: SET_USERS, users})
export const followAC = (userid) => ({type: FOLLOW, userid})
export const unfollowAC = (userid) => ({type: UNFOLLOW, userid})

export default usersReducer;
