import { authAPI } from "../../src/api/api";
import {stopSubmit} from "redux-form"

const SET_USER_DATA = 'SET-USER-DATA';

let initialState = {
    userId: null,
    email: null,
    login: null,
    isAuth: false,
}

const authReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_USER_DATA:
            return {
                ...state,
                ...action.payload,
            }
        default:
            return state;
    }
}
export const setAuthUserData = (userId, email, login, isAuth) => ({ type: SET_USER_DATA, payload: { userId, email, login, isAuth } });
export const getAuthUserData = () => {
    return (dispatch) => {
        authAPI.me().then(data => {
            if (data.resultCode === 0) {
                let { id, email, login } = data.data;
                dispatch(setAuthUserData(id, email, login, true));
            }
        })
    }
}

export const  login = (email, password, rememberMe) => {
    return (dispatch) => {
        let action = stopSubmit('Login', {_error: 'Email is wrong'});
                dispatch(action)
        return
        authAPI.login(email, password, rememberMe).then(data => {
            if (data.resultCode === 0) {
                dispatch(getAuthUserData());
            } else {
                let action = stopSubmit();
                dispatch(action('Login', {email: 'Email is wrong'}))
            }
        })
    }
}
export const  logout = (email, password, rememberMe) => {
    return (dispatch) => {
        authAPI.logout(email, password, rememberMe).then(data => {
            if (data.resultCode === 0) {
                dispatch(setAuthUserData(null, null, null, false));
            }
        })
    }
}

export default authReducer;
