import React from "react";
import { NavLink } from "react-router-dom";
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import s from "./Dialogs.module.css";

const Dialogs = (props) => {

    let dialogsElements = props.state.dialogs.map(d => <DialogItem id={d.id} name={d.name} img={d.img} />);

    let messagesElements = props.state.messages.map(m => <Message message={m.message} img={props.state.dialogs.img} />)
    let newMessageElement = React.createRef();
    let sendMessage = () => {
        let text = newMessageElement.current.value;
        props.sendMessage(text);
    }
    let onMessageChange = () => {
        let text = newMessageElement.current.value;
        updateNewMessageText(text);
    }
    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
                {dialogsElements}
            </div>
            <div className={s.messages}>
                <div className={s.writeMessages}>
                    <button onClick={sendMessage}>Send</button>
                    <input onChange={onMessageChange} ref={newMessageElement} type='text'></input>
                </div>
                <div className={s.message}>{messagesElements}</div>
            </div>
        </div>
    )
}

export default Dialogs;
