import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { openUserProfile, getStatus, updateStatus } from "../../redux/profile-reducer";
import Profile from "./Profile";
import { Navigate, useLocation, useNavigate, useParams } from "react-router-dom";
import { compose } from "redux";


const ProfileContainerWithHooks = (props) => {
  debugger
  let userId = props.router.params['*'];
  useEffect(() => {
    if (!userId) {
      userId = props.authorizationUserId
      if (!userId) {
        return <Navigate to='/login' />
      }
    } else
    openUserProfile(userId)
    getStatus(userId)
  }, [userId])

  return (
    <div>
      <Profile {...props}
        profile={props.profile}
        status={props.status}
        updateStatus={props.updateStatus} />
    </div>
  )
}

let mapStateToProps = (state) => ({
  profile: state.profilePage.profile,
  status: state.profilePage.status,
  authorizationUserId: state.auth.userId,
  isAuth: state.auth.isAuth
});

function withRouter(Component) {
  function ComponentWithRouterProp(props) {
    let location = useLocation();
    let navigate = useNavigate();
    let params = useParams();
    return (
      <Component
        {...props}
        router={{ location, navigate, params }}
      />
    );
  }
  return ComponentWithRouterProp;
}

export default compose(
  connect(mapStateToProps, { openUserProfile, getStatus, updateStatus }),
  withRouter,
)(ProfileContainerWithHooks)
