import React from "react";
import { NavLink } from "react-router-dom";
import s from "./Dialogs.module.css";

const DialogItem = (props) => {
    return (
        <div className={s.dialog}>
            <NavLink to={"/dialogs/" + props.id} className = { navData => navData.isActive ? s.active : s.dialog }>{props.name}</NavLink>
        </div>

    )
}
const Dialogs = () => {
    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
                <DialogItem id="1" name="Alex"/>
                <DialogItem id="2" name="Liudmila"/>
                <DialogItem id="3" name="Dima"/>
                <DialogItem id="4" name="Sveta"/>
                <DialogItem id="5" name="Bogdan"/>
                <DialogItem id="6" name="Lena"/>
                <DialogItem id="7" name="Artur"/>
                <DialogItem id="8" name="Stepan"/>
                <DialogItem id="9" name="Alexey"/>
                <DialogItem id="10" name="Ivan"/>
            </div>
            <div className={s.messages}>
                <div className={s.message}>Hi</div>
                <div className={s.message}>How are you?</div>
                <div className={s.message}>Oh, i'm ok, and you?</div>
            </div>
        </div>
    )
}

export default Dialogs;
