    }
    render() {
        return <Header {...this.props} />
    }

}

const mapStateToProps = (state) => ({
    isAuth: state.auth.isAuth,
    login: state.auth.login,
});
export default compose(
    connect(mapStateToProps, {getAuthUserData})
)
    (HeaderContainer)
