import React from "react";
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import s from "./Dialogs.module.css";

const Dialogs = (props) => {

    let dialogsElements = props.dialogs.map(d => <DialogItem id={d.id} name={d.name} img={d.img} status={d.status}/>);

    let messagesElements = props.messages.map(m => <Message message={m.message} img={props.dialogs.img} />)
    let newMessageElement = React.createRef();
    let onSendMessage = () => {
        props.sendMessage();
    }
    let onMessageChange = () => {
        let text = newMessageElement.current.value;
        props.updateNewMessageText(text)
    }
    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
                {dialogsElements}
            </div>
            <div className={s.messages}>
                <div className={s.writeMessages}>
                    <button onClick={onSendMessage}>Send</button>
                    <input onChange={onMessageChange} ref={newMessageElement} value={props.newMessageText} type='text'></input>
                </div>
                <div className={s.message}>{messagesElements}</div>
            </div>
        </div>
    )
}

export default Dialogs;
