import React from "react";
import { NavLink } from "react-router-dom";
import s from "./Dialogs.module.css";

const DialogItem = (props) => {
    let path = "/dialogs/" + props.id;
    return (
        <div className={s.dialog}>
            <NavLink to={path} className = { navData => navData.isActive ? s.active : s.dialog }>{props.name}</NavLink>
        </div>

    )
}
const Message = (props) => {
    return (
        <div className={s.message}>{props.message}</div>
    )
}
const Dialogs = () => {
    let dialogsData = [
        {id: 1, name: 'Alex'},
        {id: 2, name: 'Liudmila'},
        {id: 3, name: 'Dima'},
        {id: 4, name: 'Sveta'},
        {id: 5, name: 'Bogdan'},
        {id: 6, name: 'Lena'},
        {id: 7, name: 'Artur'},
        {id: 8, name: 'Stepan'},
        {id: 9, name: 'Alexey'},
        {id: 10, name: 'Ivan'},
    ]
    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
                <DialogItem id="1" name="Alex"/>
                <DialogItem id="2" name="Liudmila"/>
                <DialogItem id="3" name="Dima"/>
                <DialogItem id="4" name="Sveta"/>
                <DialogItem id="5" name="Bogdan"/>
                <DialogItem id="6" name="Lena"/>
                <DialogItem id="7" name="Artur"/>
                <DialogItem id="8" name="Stepan"/>
                <DialogItem id="9" name="Alexey"/>
                <DialogItem id="10" name="Ivan"/>
            </div>
            <div className={s.messages}>
                <Message message="Hi" />
                <Message message="How are you?" />
                <Message message="I'm fine, and you?" />
            </div>
        </div>
    )
}

export default Dialogs;
