import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import s from './Header.module.css';
import userPhoto from '../../assets/images/user-header.png';

const Header = (props) => {
    let [myProfile, setMyProfile] = useState(props.myProfile)
    useEffect(() => {setMyProfile(props.myProfile)}, [props.myProfile])
    return (
        <header className={s.header}>
            <span className={s.websiteName}>New Social</span>
            <span className={s.loginBlock}>
                {props.isAuth 
                ? (<span>
                    <img src={myProfile != null 
                    ? (myProfile.photos.small != null ? myProfile.photos.small : userPhoto)
                    : userPhoto}></img>
                    <svg fill="none" height="8" viewBox="0 0 12 8" width="12" xmlns="http://www.w3.org/2000/svg"><path clip-rule="evenodd" d="M2.16 2.3a.75.75 0 011.05-.14L6 4.3l2.8-2.15a.75.75 0 11.9 1.19l-3.24 2.5c-.27.2-.65.2-.92 0L2.3 3.35a.75.75 0 01-.13-1.05z" fill="currentColor" fill-rule="evenodd"></path></svg>
                    </span>) 
                : <NavLink to={'/login'}>Login</NavLink>}
            </span>
        </header>
    )  
};

export default Header;
